let servicesList = [];

msg.payload.forEach(service => {
    servicesList.push({
        name: service.name || '-',
        version: service.version || '-',
        state: 'OK',
        actions: service.actions ? Object.keys(service.actions).length : 0,
        events: service.events ? Object.keys(service.events).length : 0,
        // Exibe o nome de cada nó em uma nova linha
        nodes: service.nodes ? service.nodes.map(node => `<span style='color: gray;'>${node}</span>`).join('<br>') : 'N/A'
    });
});

msg.payload = servicesList;
return msg;

let actionsList = [];

msg.payload.forEach(action => {
    actionsList.push({
        name: action.name || '-',
        nodes: action.count ? `<span style="color: gray;">(*) ${action.count} <br>${action.hasLocal ? '<local>' : ''} ${action.nodes ? action.nodes.join('<br>') : ''}</span>` : 'N/A',
        state: action.available ? 'OK' : 'OFFLINE',
        cached: action.action.cache ? 'Yes' : 'No',
        params: action.action.params ? Object.keys(action.action.params).join(', ') : 'N/A'
    });
});

msg.payload = actionsList;
return msg;

// Process JSON nodes

let nodesList = [];

msg.payload.forEach(node => {
    nodesList.push({
        id: node.id || '-',
        available: node.available ? 'OK' : 'OFFLINE',
        local: node.local ? 'Yes' : 'No',
        lastHeartbeat: new Date(node.lastHeartbeatTime).toLocaleString(),
        cpu: `${node.cpu}%`,
        ipList: node.ipList ? node.ipList.join('<br>') : 'N/A',
        langVersion: node.client.langVersion || 'N/A'
    });
});

msg.payload = nodesList;
return msg;

