}

// Monta linhas com contador
let linhas = ["Time[s]    Setpoint[%]    Process Variable[%]"];

let contador = 0;
for (let i = 0; i < dada_plot1.length; i += 2) {
    let setpoint = "", pv = "";

    // Verifica o tipo e organiza
    if (dada_plot1[i].type === "Setpoint") {
        setpoint = dada_plot1[i].value;
        if (dada_plot1[i + 1] && dada_plot1[i + 1].type === "Pv") {
            pv = dada_plot1[i + 1].value;
        }
    } else if (dada_plot1[i].type === "Pv") {
        pv = dada_plot1[i].value;
        if (dada_plot1[i + 1] && dada_plot1[i + 1].type === "Setpoint") {
            setpoint = dada_plot1[i + 1].value;
        }
    }

    linhas.push(`${String(contador).padEnd(9)} ${String(setpoint).padEnd(10)} ${pv}`);
    contador++;
}

msg.payload = linhas.join("\n");
msg.headers = {
    'Content-Type': 'text/plain',
    'Content-Disposition': 'attachment; filename="Export_SpxPv.txt"'
};

return msg;

