let respostaAtual = msg.payload;
let ultimaResposta = flow.get("ultimaResposta") || null;

if (respostaAtual === "00.00" || "0") {
    // Sempre aceita "00.00", sem reset ou repetição
    msg.igual = false;
    msg.forcado = true; // usado só pra debug se quiser
    return msg;
}

msg.igual = respostaAtual === ultimaResposta;

if (!msg.igual) {
    flow.set("ultimaResposta", respostaAtual);
    flow.set("tentativas", 0);
} else {
    let tentativas = flow.get("tentativas") || 0;
    tentativas++;
    flow.set("tentativas", tentativas);
    msg.tentativas = tentativas;
    msg.parar = tentativas >= 5;
}

