// Inicializa o array se necessário
if (!flow.get("tempos_Sensor")) {
    flow.set("tempos_Sensor", []);
}

let tempos_Sensor = flow.get("tempos_Sensor");
let tempo = Date.now() - msg.startTime;
tempos_Sensor.push(tempo);
flow.set("tempos_Sensor", tempos_Sensor);

// Quando atingir 300 amostras, calcula a média e o desvio padrão
if (tempos_Sensor.length >= 300) {
    // Cálculo da média
    let soma = tempos_Sensor.reduce((a, b) => a + b, 0);
    let media = soma / tempos_Sensor.length;

    // Cálculo do desvio padrão
    let variancia = tempos_Sensor.reduce((acc, val) => {
        return acc + Math.pow(val - media, 2);
    }, 0) / tempos_Sensor.length;

    let desvioPadrao = Math.sqrt(variancia);

    msg.payload = `Serviço de Sensor: Média (300 amostras): ${media.toFixed(2)} ms Desvio padrão: ${desvioPadrao.toFixed(2)} ms`;

    // Limpa o array para a próxima rodada
    flow.set("tempos_Sensor", []);
    return msg;
} else {
    return null;
}

