tempos.push(tempo);
flow.set("tempos", tempos);

// Quando atingir 300 amostras, calcula a média e o desvio padrão
if (tempos.length >= 300) {
    // Cálculo da média
    let soma = tempos.reduce((a, b) => a + b, 0);
    let media = soma / tempos.length;

    // Cálculo do desvio padrão
    let variancia = tempos.reduce((acc, val) => {
        return acc + Math.pow(val - media, 2);
    }, 0) / tempos.length;

    let desvioPadrao = Math.sqrt(variancia);

    msg.payload = `Node-red: Média (300 amostras): ${media.toFixed(2)} ms Desvio padrão: ${desvioPadrao.toFixed(2)} ms`;

    // Limpa o array para a próxima rodada
    flow.set("tempos", []);
    return msg;
} else {
    return null;
}

